module.exports = {
	module: {
		rules: [
			{
				test: /\.pug$/,
				use: [
					"pug-loader?self",
				]
			},
			{
				test: /\.css$/,
				use: [
					"style-loader",
					"css-loader",
				],
			},
			{
				test: /\.scss$/,
				use: [{
						loader: "style-loader"
					},
					{
						loader: "css-loader"
					},
					{
						loader: "sass-loader"
					}]
			}
		]
	}
};
